Create database PingPong;
GO 
Use PingPong;


CREATE TABLE Jugador-- La cédula de identidad del cliente y la fotografía deberán almacenarse en la base de datos como una imagen.
(
Jugador_Id int primary key,
Identificacion varchar,
FotoCedula varbinary(MAX),
Jugador_Nombre varchar(50),
Nacionalidad varchar(50),
CodigoSexo char,
FechaNacimiento datetime,
Edad int,
Jugador_Foto varbinary(MAX),
Telefono varchar(20),
CorreoElectronico varchar(50),
Direccion varchar (50),
CodigoEquipo int,
ranking INT,  
Estado bit, --Sólo se podrá modificar o eliminar un participante que se encuentren en estado ACTIVO 
);

CREATE TABLE Equipo --Tabla para procesos de competencia
(
Equipo_Id int primary key,
Equipo_Nombre varchar(255),
Estado bit, --Sólo se podrá modificar o eliminar un equipo que se encuentren en estado ACTIVO
);


CREATE TABLE Sexo
(
Codigo char primary key ,
Nombre varchar(50)
);


