CREATE TABLE Partidos2 --Competencia
(
    Partido_id INT PRIMARY KEY,
    Equipo1_id INT,
    Equipo2_id INT,
    fecha DATE,
	Estado bit,
    FOREIGN KEY (Equipo1_id) REFERENCES Equipo(Equipo_Id),
	FOREIGN KEY (Equipo2_id) REFERENCES Equipo(Equipo_Id),
);

CREATE TABLE Sets
(
	Set_Id INT PRIMARY KEY,
	Partidos2_Id INT,
	Set_Number INT,
	Equipo1_Puntos INT,
	Equipo2_Puntos INT,
	FOREIGN KEY(Partidos2_Id) REFERENCES Partidos2(Partido_id),
);

CREATE TABLE Torneo --Tabla para procesos de competencia
(
	Torneo_Id int primary key,
	Torneo_Nombre varchar(255),
	Promotora varchar(100),
	FechaInicio date,
	FechaFinal date,
	CostoCRC money,
	CostoUSD money, 
	Estado bit --Sólo se podrá modificar o eliminar los torneos que se encuentren en estado ACTIVO. Una vez que existe una inscripción ACTIVA asociada al torneo no podrá ser modificada.
);

CREATE TABLE Torneo_Jugador --Tabla para procesos de competencia
(
	Torneo_Id int,
	Jugador_Id int,
	FOREIGN KEY (Torneo_Id) REFERENCES Torneo(Torneo_Id),
	FOREIGN KEY (Jugador_Id) REFERENCES Jugador(Jugador_Id),
);


CREATE TABLE Torneo_Partidos2(
	Torneo_Id INT,
	Partidos2_Id INT,
	FOREIGN KEY (Torneo_id) REFERENCES Torneo(Torneo_Id),
	FOREIGN KEY (Partidos2_id) REFERENCES Partidos2(Partido_Id)
);

CREATE TABLE LugarEncuentro (
  Lugar_Id INT PRIMARY KEY,
  Lugar_Nombre VARCHAR(255),
  Lugar_Ubicacion VARCHAR(255)
);

CREATE TABLE Partidos2_LugarEncuentro(
  Partido_Id INT,
  Lugar_Id INT,
  FOREIGN KEY (Partido_Id) REFERENCES Partidos2(Partido_Id),
  FOREIGN KEY (Lugar_Id) REFERENCES LugarEncuentro(Lugar_Id)
);

CREATE TABLE Arbitro(
  Arbitro_Id INT PRIMARY KEY,
  Arbitro_Nombre VARCHAR(255),
  Arbitro_Nivel VARCHAR(255)
);


CREATE TABLE Inscripciones
(
	CodigoTorneo int,
	CodigoParticipante int,
	ModalidadPago varchar(50), --tarjeta débito, tarjeta crédito, sinpe, efectivo
	MontoPagoCRC money,
	MontoPagoUSD money,
	FechaInscripcion datetime,
	TipoCambio money,
	Estado bit
);

CREATE TABLE Roles --Tabla para el logIn
(
	Codigo int primary key,
	Nombre varchar(50)
);

CREATE TABLE Usuarios   --Tabla para el logIn
(
	Usuario varchar(50),
	Contraseña varchar(50),
	NombreCompleto varchar (100),
	fotoUsuario varbinary(MAX),
	codigoRol int
);

